import React, {useState} from 'react';

const Single_todo = ({key, todo, DelTodo}) => {
  const [todo_text, setTodoText] = useState(todo);
  const [state, setState] = useState("Todo"); //Todo:項目にあるのみ Doing:作業中 Done:完了

  const changeState = () => {
    if(state === "Todo"){
      setState(state, "Doing");

    }
    else if(state === "Doing"){
      setState(state, "Done");
      DelTodo(state);
    }
  }

  return (
    <div>
      <li key={key}>{todo}</li>
      <button onClick={changeState}>{state}</button>
    </div>
  )
}
