import React, {useState} from 'react';
import { ViewList } from './ViewList';
import { Edit } from "./Edit";

import { Todo_data } from './common/Todo_data';

const App = () => {
  const [Todos, setTodo] = useState(Todo_data);
  const [EditState, setEditState] = useState(''); //Edit:編集中

  // Todoリストに追加
  const AddTodo = (Todo) => {
    // Todo_data.append(Todo);
    console.log(Todo);
    setTodo([...Todos, Todo]);
  }

  // Todoリストから削除
  // 参考 https://qiita.com/itachi/items/4184b2afc35b55b45568
  const DelTodo = (Todo) => {
    setTodo(
      Todos.filter((todo, i) => (todo !== Todo))
    )
  }

  // Todoリストの内容を変更
  const ChangeTodo = (ChangeItem, Index) => {
    if(EditState === ''){
      setEditState('Edit');
    }
    setTodo(
      Todos.map((todo, i) => (i === Index ? ChangeItem : todo))
    )
  }

  return(
    <div>
      <ViewList EditState={EditState} TodoData={Todos} ChangeTodo={ChangeTodo} DelTodo={DelTodo}/>
      <Edit AddTodo={AddTodo} />
    </div>
  )
}

export default App;
