import React, {useState} from 'react';

export const SingleTodo = ({todo, ChangeTodo, DelTodo}) => {
  const [state, setState] = useState("Todo"); //Todo:項目にあるのみ Doing:作業中 Done:完了
  const [EditState, setEditState] = useState(''); //Edit:編集中

  const changeState = () => {
    if(state === "Todo"){
      setState("Doing");
    }
    else if(state === "Doing"){
      setState("Done");
    }
    console.log(`${todo} ${state}`);
  }

  const changeTodo = () => {
    ChangeTodo(todo);
  }

  const delTodo = () => {
    DelTodo(todo);
  }

  return (
    <div className='SingleTodo'>
      {
        EditState === '' ? <li>{todo}</li> : <input type="text"></input>
      }
      <button onClick={changeTodo}>Edit</button>
      <button onClick={changeState}>{state}</button>
      <button onClick={delTodo}>Delete</button>
    </div>
  )
}
